import { useState } from 'react'

export default function Calculator () {

    const [ result, setResult ] = useState(0);

    let currentCalc='';

    const handleClick = (event) => {
        event.preventDefault();
        setDisplay (event.target.value);
        currentCalc = currentCalc + event.target.value;
    }

    const handleSubmit = (event) => {
        setDisplay(eval(currentCalc));
    }

    const resetCalc = () => {
        setDisplay(0);
        currentCalc = '';
    }

    return (
        <main id="calculator-container">
        <section id="result"><h1>0</h1></section>
        <section id="buttons-container">
            <div className="calc-button tertiary-btn" onClick={resetCalc}>AC</div>
            <div className="calc-button tertiary-btn" onClick={handleClick}><span className="material-symbols-rounded size-48">backspace</span></div>
            <div className="calc-button tertiary-btn" value="%" onClick={handleClick}>&percnt;</div>
            <div className="calc-button primary-btn" value="/" onClick={handleClick}>&div;</div>
            <div className="calc-button primary-container-btn" value="7" onClick={handleClick}>7</div>
            <div className="calc-button primary-container-btn" value="8" onClick={handleClick}>8</div>
            <div className="calc-button primary-container-btn" value="9" onClick={handleClick}>9</div>
            <div className="calc-button primary-btn" value="*" onClick={handleClick}>&times;</div>
            <div className="calc-button primary-container-btn" value="4" onClick={handleClick}>4</div>
            <div className="calc-button primary-container-btn" value="5" onClick={handleClick}>5</div>
            <div className="calc-button primary-container-btn" value="6" onClick={handleClick}>6</div>
            <div className="calc-button primary-btn" value="-" onClick={handleClick}>&minus;</div>
            <div className="calc-button primary-container-btn" value="1" onClick={handleClick}>1</div>
            <div className="calc-button primary-container-btn" value="2" onClick={handleClick}>2</div>
            <div className="calc-button primary-container-btn" value="3" onClick={handleClick}>3</div>
            <div className="calc-button primary-btn" onClick={handleClick}>&plus;</div>
        </section>
        <section id="bottom-btns">
            <div className="calc-button primary-container-btn" id="zero-btn" value="0" onClick={handleClick}>0</div>
            <div className="calc-button primary-container-btn" value="." onClick={handleClick}>&period;</div>
            <div className="calc-button primary-btn" onClick={handleClick}>&equals;</div>
        </section>
     </main>
    )
}
